class Bird(Animal):
    def speak(self):
        print(f"{self.name} chirps!")

animals = [Dog("Buddy", "Dog"), Cat("Whiskers", "Cat"), Bird("Tweety", "Bird")]

for animal in animals:
    animal.speak()

io lang
---
duck := Object clone
duck name := "Duck"
duck speak := method("quack" println)

# 动态修改 slot
duck walk := method("waddle" println)
duck speak  # 输出：quack
duck walk   # 输出：waddle

javascript
---

// 创建一个对象
const duck = {
  name: "Duck",
  speak: function () {
    console.log("quack");
  }
};

// 动态添加或修改方法
duck.walk = function () {
  console.log("waddle");
};

// 调用方法
duck.speak(); // 输出：quack
duck.walk();  // 输出：waddle

