// 创建一个对象
const duck = {
  name: "Duck",
  speak: function () {
    console.log("quack");
  }
};

// 动态添加或修改方法
duck.walk = function () {
  console.log("waddle");
};

// 调用方法
duck.speak(); // 输出：quack
duck.walk();  // 输出：waddle

function add(x, y) {
    return x + y;
}

let result = add(2, 3);  // result = 5

class Cons {
  constructor(h, t) {
    this.h = h
    this.t = t
  }
  toString() {
    function iter(that) {
      if (that.t == null) return that.h
      return that.h + ", " + iter(that.t)
    }
    return "[" + iter(this) + "]"
  }

  // 通过递归的方式遍历链表，并将一个函数 f 应用到每一个元素上，生成一个新的链表。
  map(f) {
    if (this.t == null) return new Cons(f(this.h), null)
    return new Cons(f(this.h), this.t.map(f))
  }

  //  通过循环遍历整个链表，直接修改每个元素。
  // 展示了操作数据的不同策略：递归与迭代。
  map2(f) {
    let it = this
    while (it != null) {
      it.h = f(it.h)
      it = it.t
    }
  }
}

const cons = (h, t) => new Cons(h, t)
const list = (...args) => {
  if (args.length == 0) return null
  return cons(args[0], list(...args.slice(1)))
}

const c = list(2, 3, 4, 5, 6)
console.log(list(2, 5, 8).map(x => x * 2).toString())
c.map2(x => x + 8)
console.log(c.toString())

